import React from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import { Card, Badge, Collapse, Row, Col, Nav, Navbar } from "react-bootstrap";
import Navbarr from "../../UI/Navbar";
import axios from "axios";
import { Component } from "react";

export default class FormJob extends Component {
  constructor(props) {
    super(props);
    this.state = {
      organisation: "",
      designation: "",
      basesalary: "",
      location: "",
      workhours: "",
      contactNo: "",
    };
  }

  onChange = (e) => {
    /*
        Because we named the inputs to match their
        corresponding values in state, it's
        super easy to update the state
      */
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();
    // get our form data out of state
    const {
      organisation,
      designation,
      basesalary,
      location,
      workhours,
      contactNo,
    } = this.state;

    axios.post("https://voice4rural.herokuapp.com/jobs", {
      organisation,
      designation,
      basesalary,
      location,
      workhours,
      contactNo,
    });
  };
  render() {
    const {
      organisation,
      designation,
      basesalary,
      location,
      workhours,
      contactNo,
    } = this.state;

    return (
      <form
        noValidate
        onSubmit={this.onSubmit}
        style={{
          width: "100%", // Fix IE 11 issue.
          marginTop: theme.spacing(1),
        }}
      >
        <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          id="item"
          label="Company Name"
          value={organisation}
          name="organisation"
          type="text"
          autoComplete="item"
          autoFocus
          onChange={this.onChange}
        />
        <TextField
          variant="outlined"
          margin="normal"
          halfWidth
          required
          value={designation}
          name="designation"
          label="Job Role"
          type="text"
          id="margin-normal"
          autoComplete="number"
          className="mr-5"
          onChange={this.onChange}
        />
        <TextField
          variant="outlined"
          margin="normal"
          required
          halfWidth
          value={location}
          name="location"
          label="Location"
          type="text"
          id="margin-normal"
          autoComplete="number"
          className="mr-5"
          onChange={this.onChange}
        />
        <TextField
          variant="outlined"
          margin="normal"
          halfWidth
          required
          value={basesalary}
          name="basesalary"
          label="Salary"
          type="number"
          id="margin-normal"
          autoComplete="number"
          onChange={this.onChange}
        />
        <TextField
          variant="outlined"
          margin="normal"
          halfWidth
          required
          value={workhours}
          name="workhours"
          label="Work Hours"
          type="number"
          id="margin-normal"
          autoComplete="number"
          className="mr-5"
          onChange={this.onChange}
        />
        <TextField
          variant="outlined"
          margin="normal"
          halfWidth
          required
          value={contactNo}
          name="contactNo"
          label="Contact Number"
          type="number"
          id="margin-normal"
          autoComplete="number"
          onChange={this.onChange}
        />
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          style={{ margin: theme.spacing(3, 0, 2) }}
        >
          Post
        </Button>
      </form>
    );
  }
}
