import React, { useState, useEffect } from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import { Card, Badge, Collapse, Row, Col, Nav, Navbar } from "react-bootstrap";
import Navbarr from "../../UI/Navbar";
import axios from "axios";
import { Component } from "react";
import FormJob from "./PostJob";

const useStyles = makeStyles((theme) => ({
  root: {
    height: "100vh",
  },
  image: {
    backgroundColor: "white",
    backgroundColor:
      theme.palette.type === "light"
        ? theme.palette.grey[50]
        : theme.palette.grey[900],
    backgroundSize: "cover",
    backgroundPosition: "center",
  },
  paper: {
    margin: theme.spacing(8, 4),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
}));

export default function Jobs() {
  const [jobs, setJobs] = useState([]);
  useEffect(() => {
    axios
      .get("https://voice4rural.herokuapp.com/jobs")
      .then((res) => {
        console.log(res.data.jobs);
        setJobs(res.data.jobs);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const classes = useStyles();

  return (
    <>
      <Navbarr />
      <Navbar
        collapseOnSelect
        expand="lg"
        variant="light"
        style={{
          backgroundColor: "transparent",
          boxShadow: "2px 1px 3px lightgray",
        }}
      >
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="/githubjobs">Other Jobs</Nav.Link>
            <Nav.Link href="/jobs">Rural Jobs</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      <Grid container component="main" className={classes.root}>
        <CssBaseline />
        <Grid
          item
          xs={false}
          sm={4}
          md={7}
          className={classes.image}
          data-spy="scroll"
        >
          {jobs.map((job) => (
            <Card className="m-lg-2" key={job._id.$oid}>
              <Card.Header
                style={{ backgroundColor: "#f50057", color: "#fff" }}
              >
                {job.organisation}
              </Card.Header>
              <Card.Body>
                <Card.Title>{job.designation}</Card.Title>
                <Card.Text>
                  <p>
                    Location: <strong> {job.location}</strong>
                  </p>
                  <p>
                    Contact No: <strong> {job.contactNo}</strong>
                  </p>
                  <p>
                    Work hours: <strong> {job.workhours}</strong>
                  </p>
                  <p>
                    Base Salary: <strong> Rs. {job.basesalary}</strong>
                  </p>
                </Card.Text>
              </Card.Body>
            </Card>
          ))}
        </Grid>
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
          <div className={classes.paper}>
            <Typography component="h1" variant="h5">
              Post a Job!
            </Typography>
            <FormJob />
          </div>
        </Grid>
      </Grid>
    </>
  );
}
