import React from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Link from "@material-ui/core/Link";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Navbarr from "../../UI/Navbar";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import vaccine from "../../assets/vaccine.gif";

class Vaccine extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dates: {},
    };
  }

  componentDidMount() {
    axios
      .get("https://voice4rural.herokuapp.com/vaccine")
      .then((res) => this.setState({ fact: res.data }))
      .catch(alert);
    //   axios
    //     .get("http://randomquote-sakshi.azurewebsites.net/riddles/")
    //     .then((res) => this.setState({ riddle: res.data }))
    //     .catch(alert);
  }

  render() {
    const { dates } = this.state;
    return (
      <>
        <Navbarr />
        <Container
          component="main"
          maxWidth="md"
          style={{ boxShadow: "1px 1px 1px 1px lightgray" }}
          className="p-5 my-3"
        >
          <CssBaseline />
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Grid item xs={12} sm={6}>
              <div className="ml-5">
                <img src={vaccine} alt="vaccine" style={{ width: "50%" }} />
              </div>
            </Grid>
            <Typography component="h1" variant="h5">
              Book Your Vaccination Slot
            </Typography>
            <form style={{ width: "100%" }} noValidate>
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField
                    autoComplete="fname"
                    name="name"
                    variant="outlined"
                    required
                    fullWidth
                    id="firstName"
                    label="Your Name"
                    autoFocus
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    variant="outlined"
                    required
                    fullWidth
                    id="lastName"
                    type="number"
                    label="Total number of Members"
                    name="lastName"
                    autoComplete="lname"
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    variant="outlined"
                    required
                    fullWidth
                    id="email"
                    type="text"
                    label="Your Phone Number"
                    name="phone"
                  />
                </Grid>
                <Grid item xs={12}>
                  <FormControl component="fieldset">
                    <FormLabel component="legend">Choose a Date</FormLabel>
                    <RadioGroup
                      aria-label="gender"
                      name="gender1"
                      //value={value}
                      //onChange={handleChange}
                    >
                      {dates.map((date) => (
                        <FormControlLabel
                          //value="female"
                          control={<Radio />}
                          label={date}
                        />
                      ))}
                    </RadioGroup>
                  </FormControl>
                </Grid>
              </Grid>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
              >
                Book
              </Button>
            </form>
          </div>
        </Container>
      </>
    );
  }
}
