import React from "react";
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import { Card, Badge, Collapse, Row, Col, Nav, Navbar } from "react-bootstrap";
import Navbarr from "../../UI/Navbar";
import axios from "axios";

const useStyles = makeStyles((theme) => ({
  root: {
    height: "100vh",
  },
  image: {
    backgroundColor: "white",
    backgroundColor:
      theme.palette.type === "light"
        ? theme.palette.grey[50]
        : theme.palette.grey[900],
    backgroundSize: "cover",
    backgroundPosition: "center",
  },
  paper: {
    margin: theme.spacing(8, 4),
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: "100%", // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default class Jobs extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      jobs: [],
    };
  }

  componentDidMount() {
    const apiUrl = "https://voice4rural.herokuapp.com/jobs";

    fetch(apiUrl)
      .then((res) => res.json())
      .then(
        (result) => {
          this.setState({
            jobs: result,
          });
        },
        (error) => {
          this.setState({ error });
        }
      );
  }

  render() {
    const { error, jobs } = this.state;
    const classes = useStyles();
    if (error) {
      return <div>Error: {error.message}</div>;
    } else {
      return (
        <>
          <Navbarr />
          <Navbar
            collapseOnSelect
            expand="lg"
            variant="light"
            style={{
              backgroundColor: "transparent",
              boxShadow: "2px 1px 3px lightgray",
            }}
          >
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link href="/githubjobs">Other Jobs</Nav.Link>
                <Nav.Link href="/jobs">Rural Jobs</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
          <Grid container component="main" className={classes.root}>
            <CssBaseline />
            <Grid
              item
              xs={false}
              sm={4}
              md={7}
              className={classes.image}
              data-spy="scroll"
            >
              {jobs.map((job) => (
                <Card className="m-lg-2" key={job.id}>
                  <Card.Header
                    style={{ backgroundColor: "#f50057", color: "#fff" }}
                  >
                    {job.organisation}
                  </Card.Header>
                  <Card.Body>
                    <Card.Title>{job.designation}</Card.Title>
                    <Card.Text>
                      <h3>
                        Location: <strong> {job.location}</strong>
                      </h3>
                      <h3>
                        Contact No: <strong> {job.contactNo}</strong>
                      </h3>
                      <h3>
                        Work hours: <strong> {job.workhours}</strong>
                      </h3>
                      <h3>
                        Base Salary: <strong> Rs. {job.basesalary}</strong>
                      </h3>
                    </Card.Text>
                  </Card.Body>
                </Card>
              ))}
            </Grid>
            <Grid
              item
              xs={12}
              sm={8}
              md={5}
              component={Paper}
              elevation={6}
              square
            >
              <div className={classes.paper}>
                <Typography component="h1" variant="h5">
                  Post a Job!
                </Typography>
                <form className={classes.form} noValidate>
                  <TextField
                    variant="outlined"
                    margin="normal"
                    required
                    fullWidth
                    id="item"
                    label="Company Name"
                    name="item"
                    type="text"
                    autoComplete="item"
                    autoFocus
                  />
                  <TextField
                    variant="outlined"
                    margin="normal"
                    halfWidth
                    required
                    name="number"
                    label="Job Role"
                    type="text"
                    id="margin-normal"
                    autoComplete="number"
                    className="mr-5"
                  />
                  <TextField
                    variant="outlined"
                    margin="normal"
                    required
                    halfWidth
                    name="number"
                    label="Location"
                    type="text"
                    id="margin-normal"
                    autoComplete="number"
                    className="mr-5"
                  />
                  <TextField
                    variant="outlined"
                    margin="normal"
                    halfWidth
                    required
                    name="number"
                    label="Salary"
                    type="number"
                    id="margin-normal"
                    autoComplete="number"
                  />
                  <TextField
                    variant="outlined"
                    margin="normal"
                    halfWidth
                    required
                    name="number"
                    label="Work Hours"
                    type="number"
                    id="margin-normal"
                    autoComplete="number"
                    className="mr-5"
                  />
                  <TextField
                    variant="outlined"
                    margin="normal"
                    halfWidth
                    required
                    name="number"
                    label="Contact Number"
                    type="number"
                    id="margin-normal"
                    autoComplete="number"
                  />
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    color="primary"
                    className={classes.submit}
                  >
                    Post
                  </Button>
                </form>
              </div>
            </Grid>
          </Grid>
        </>
      );
    }
  }
}
